const { app, BrowserWindow, ipcMain } = require( "electron" );
const Path = require( "path" );
const URL = require( "url" );
const { Fork } = require( "child_process" );

const Three = require( "three" );

// Keep a global reference of the window object, if you don't, the window will
// be closed automatically when the JavaScript object is garbage collected.
const windows = {};

function createPlayWindow() {

	// Create the browser window.
	windows.play = new BrowserWindow({
		width: 800,
		height: 600,
		center: true,
		resizable: true,
		frame: true,
		transparent: false
	});
	windows.play.setMenu( null );

	// and load the index.html of the app.
	windows.play.loadURL( URL.format({
		pathname: Path.join( app.getAppPath(), "src/windows/play.html" ),
		protocol: "file:",
		slashes: true
	}) );

	// Open the DevTools.
	windows.play.webContents.openDevTools();

	// Emitted when the window is closed:
	windows.play.on( "closed", () => {
		delete windows.play;
	});
}

// Create window when app is ready:
app.on( "ready", () => {
	createPlayWindow();
});

// Quit when all windows are closed:
app.on( "window-all-closed", () => {
	app.quit();
});

// Re-create window if somehow it went missing:
app.on( "activate", () => {
	if ( windows.play === null ) {
		createPlayWindow();
	}
});

// Send simulation updates:
const loopTimer = setInterval( () => {
	windows.play.webContents.send( "info", {
		msg: "Simulation update from main process."
	});
}, 2000 );

/*
	The plugin manager is a singleton which loads plugins depending on the plugin
	order. This order is loaded from preferences, and then any no-longer existing
	packages are removed from the list, and any new packages are added to the
	list. the user can re-order the list, which, on save, will reload all plugins.
*/

import FS from "fs";
import Path from "path";

export default class {
	constructor( init = true ) {

		this._pluginDir = Path.resolve( __dirname, "../../plugins/" );

		// It's possible to avoid running the init method, but default is to do it:
		if ( !init ) {
			return this;
		}
		this._init();
	}

	// ---------------------------------------------------------------------------
	// PRIAVTE:
	// ---------------------------------------------------------------------------
	_init() {
		this.findPlugins( this._pluginDir, ( pluginData ) => {
			console.log( pluginData );
		});
	}
	_registerAssembly( json ) {
		// Do nothing.
	}

	_registerComponent( json ) {
		// Do nothing.
	}

	_registerAnimationClip() {

	}

	_registerAsset( json ) {
		this._assets.push( json );
	}

	// ---------------------------------------------------------------------------
	// PUBLIC:
	// ---------------------------------------------------------------------------
	/**
		* @param {number} dir - this is a value.
		* @param {function} callback - this is a value.
		* @return {array} result of the sum value.
	*/
	findPlugins( dir, callback ) {

		// Keep track of how many plugins have been loaded:
		let loaded = 0;
		const pluginData = [], items = FS.readdirSync( dir );

		// For each found item in the plugins directory, load it:
		items.forEach( ( item ) => {

			// If the item is a .DS_Store file, skip it, but still mark as processed:
			if ( item == ".DS_Store" ) {
				loaded++;
				return;
			}

			// Read the package.json file, and add it to the pluginData array:
			const path = Path.join( dir, item, "package.json" );
			FS.readFile( path, "utf8", ( err, data ) => {
				if ( err ) {
					throw err;
				}
				pluginData.push( JSON.parse( data ) );
				loaded++;

				// When we've loaded as many files as were found, it's time to return:
				if ( loaded === items.length ) {
					if ( typeof callback === "function" ) {
						callback( pluginData );
					}
					return pluginData;
				}
			});
		});
	}

	loadPlugins( plugins ) {
		const assetTypes = [ "texture", "sound", "model" ];
		let registered = 0;
		plugins.forEach( ( plugin )=>{
			plugin.contents.forEach( ( item )=>{
				if ( this._scanFor( item.type, assetTypes ) ) {
					this._registerAsset( item );
				}
			});
			registered++;
			if ( registered === plugins.length ) {
				console.log( Util.inspect( this._assets, { depth: null, colors: true }) );
			}
		});
	};
}

import Engine from "./Engine.js";

let app;
window.onload = function() {
	app = new Engine();
};

const Three = require( "three" );
const OrbitControls = require( "three-orbit-controls" )( Three );

export default class {
	constructor() {
		console.log( "Created a new rendering system." );
		this._mixers = [];
		this.init();
	}
	init() {
		console.log( "Initializing a new rendering system." );

		const container = document.createElement( "div" );
		console.log( document );
		document.body.appendChild( container );
		this._camera = new Three.PerspectiveCamera( 30, window.innerWidth / window.innerHeight, 1, 10000 );
		this._camera.up.set( 0, 0, 1 );
		this._camera.position.set( 8, -8, 8 );
		this._camera.lookAt( new Three.Vector3( 0, 0, 0 ) );

		const loader = new Three.JSONLoader();

		const scope = this;

		const paths = [];
		paths.push( "../../plugins/age-of-mythology/model/greek-villager-female-forage.js" );
		paths.forEach( ( path ) => {
			loader.load( path, ( geometry, materials ) => {
				for ( var i = 0; i < materials.length; i++ ) {
					materials[ i ].color.setRGB( 1.0, 1.0, 1.0 );
					materials[ i ].morphTargets = true;
				}
				const mesh = new Three.Mesh( geometry, new Three.MeshLambertMaterial({
					color: 0x999999
				}) );
				mesh.material.morphTargets = true;

				const mixer = new Three.AnimationMixer( mesh );
				const clip = Three.AnimationClip.CreateFromMorphTargetSequence( "walk", geometry.morphTargets, 30 );
				mixer.clipAction( clip ).setDuration( 1 ).play();

				scope._scene.add( mesh );
				mesh.position.z -= 1;
				scope._mixers.push( mixer );
			});
		});

		this._scene = new Three.Scene();

		let light;
		light = new Three.DirectionalLight( 0xefefff, 1 );
		light.position.set( 1, 1, 1 ).normalize();
		this._scene.add( light );
		light = new Three.DirectionalLight( 0xefccaa, 1 );
		light.position.set( -1, -1, -1 ).normalize();
		this._scene.add( light );
		this._scene.add( new Three.AmbientLight( 0x666666 ) );

		this._renderer = new Three.WebGLRenderer();
		this._renderer.setClearColor( 0x999999 );
		this._renderer.setPixelRatio( window.devicePixelRatio );
		this._renderer.setSize( window.innerWidth, window.innerHeight );
		this._renderer.antialias = false;

		container.appendChild( this._renderer.domElement );
		window.addEventListener( "resize", this._resize.bind( this ), false );

		this._controls = new OrbitControls( this._camera, this._renderer.domElement );
		this._controls.enableRotate = true;
	}
	update( delta ) {
		this._mixers.forEach( ( mixer ) => {
			mixer.update( delta / 1000 );
		});
		this._renderer.render( this._scene, this._camera );
	}
	_resize( e ) {
		this._camera.aspect = window.innerWidth / window.innerHeight;
		this._camera.updateProjectionMatrix();
		this._renderer.setSize( window.innerWidth, window.innerHeight );
	}
}

